#import google maps API's python wrapper to convert address to co-ordinates (geocoding)
import googlemaps

#get keys from the Google Maps developer console
from getKeys import gMapsKey

#create google maps API client to interact with the online servers
gmapsClient = googlemaps.Client(gMapsKey);

cityList = ['Fairbanks', 'Winnipeg', 'Vancouver', 'San Francisco', 'Los Angeles', 'Ottawa', 'Chicago', 'Dallas', 'New York', 'Washington DC', 'Houston', 'Miami', 'Mexico', 'Panama', 'Bogota', 'Caracas', 'Georgetown', 'Quito', 'Lima', 'Rio de Janeiro', 'Sao Paulo', 'Buenos Aires', 'Santiago', 'Reykjavik', 'Rabat', 'Dakar', 'Lagos', 'Cairo', 'Kinshasa', 'Pretoria', 'Cape Town', 'Addis Ababa', 'Nairobi', 'Mombasa', 'Port Louis', 'Tehran', 'Kuwait', 'Dubai', 'Karachi', 'Aden', 'Lahore', 'Mumbai', 'New Delhi', 'Kathmandu', 'Dhaka', 'Chennai', 'Colombo', 'Kolkata', 'Omsk', 'Irkutsk', 'Beijing', 'Shanghai', 'Hong Kong', 'Bangkok', 'Singapore', 'Jakarta', 'Manila', 'Tokyo', 'Seoul', 'Perth', 'Brisbane', 'Melbourne', 'Sydney', 'Auckland', 'Wellington', 'Christchurch', 'London', 'Lisbon', 'Paris', 'Berlin', 'Frankfurt', 'Rome', 'Athens', 'Oslo', 'Stockholm', 'Leningrad', 'Moscow', 'Perm', 'Ankara']

